--

CREATE PROCEDURE nms80.create_pf_next_year_partitions(IN table_name character varying, IN is_check_time boolean DEFAULT true)
    LANGUAGE plpgsql
    AS $$
DECLARE
    y INTEGER;  -- 다음 년도
    m INTEGER;  -- 월
    start_dt TEXT;
    end_dt TEXT;
    part_table_name TEXT;
BEGIN
    -- 다음 년도 구하기
    y := EXTRACT(YEAR FROM CURRENT_DATE) + 1;

    FOR m IN 1..12 LOOP
        -- 시간 포맷 처리
        IF is_check_time THEN
            -- 체크 타임 파티션
            start_dt := to_char(make_date(y, m, 1), 'YYYYMMDD') || '000000';
            end_dt   := to_char(make_date(y, m, 1) + interval '1 month', 'YYYYMMDD') || '000000';
        ELSE
            -- 시간 파티션
            start_dt := to_char(make_date(y, m, 1), 'YYYYMMDD') || '00';
            end_dt   := to_char(make_date(y, m, 1) + interval '1 month', 'YYYYMMDD') || '00';
        END IF;

        -- 파티션 테이블명: 테이블명_YYYYMM
        part_table_name := format('%s_', table_name) || to_char(make_date(y, m, 1), 'YYYYMM');

        -- 존재 여부 체크 (스키마 포함)
        IF EXISTS (
            SELECT 1 
            FROM pg_class c
            JOIN pg_namespace n ON c.relnamespace = n.oid
            WHERE c.relname = part_table_name
            AND n.nspname = 'nms80'
        ) THEN
            RAISE NOTICE 'Skip existing partition table: %', part_table_name;
            CONTINUE;
        END IF;

        -- 파티션 테이블 생성
        RAISE NOTICE 'Create Partition Table: % From: % To: %', part_table_name, start_dt, end_dt;

        EXECUTE format(
            'CREATE TABLE %I PARTITION OF %I
             FOR VALUES FROM (%L) TO (%L);',
            part_table_name, table_name, start_dt, end_dt
        );

        -- 인덱스 생성
        EXECUTE format(
            'CREATE INDEX idx_%I_time ON %I(time);',
            part_table_name, part_table_name
        );
    END LOOP;
END;
$$;


