--
-- Name: drop_pf_partitions(character varying, integer); Type: PROCEDURE; Schema: nms80; Owner: nms80
--

CREATE PROCEDURE nms80.drop_pf_partitions(IN table_name character varying, IN keep_months integer)
    LANGUAGE plpgsql
    AS $$
DECLARE
    part RECORD;
    drop_boundary VARCHAR(6);
    partition_prefix VARCHAR;
BEGIN
    partition_prefix := table_name || '_';

    drop_boundary := TO_CHAR((CURRENT_DATE - (keep_months || ' months')::INTERVAL), 'YYYYMM');

    RAISE NOTICE 'drop_boundary: %', drop_boundary;

    FOR part IN
        SELECT c.relname AS partition_name
        FROM pg_class c
        JOIN pg_namespace n ON n.oid = c.relnamespace
        JOIN pg_inherits i ON i.inhrelid = c.oid
        WHERE c.relname LIKE partition_prefix || '%'
        AND c.relkind IN ('r', 'p')
        AND RIGHT(c.relname, 6) < drop_boundary
    LOOP
        RAISE NOTICE 'Dropping partition table: %', part.partition_name;
        EXECUTE format('DROP TABLE IF EXISTS %I CASCADE', part.partition_name);
    END LOOP;
END;
$$;


