--

CREATE FUNCTION nms80.fnc_chg_asset_format(name character varying DEFAULT NULL::character varying, old_val character varying DEFAULT NULL::character varying, new_val character varying DEFAULT NULL::character varying, msg character varying DEFAULT NULL::character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
chg_msg character varying :=NULL;
begin
	
IF msg IS NOT NULL THEN
	chg_msg := '[' || name || '] '|| msg || ' ';
ELSIF old_val IS NULL AND new_val IS NOT NULL THEN
	chg_msg := '[' || name || '] ' || new_val || ' 가(이) 추가';
ELSIF  old_val IS NOT NULL AND new_val IS NOT NULL THEN
	IF old_val = new_val THEN -- old_val 과 new_val이 같으면 이력을 남기지 않는다.
	   chg_msg:= NULL;
	elseif  old_val != new_val then
		chg_msg := '[' || name || '] 가(이) ' || old_val || ' 에서 ' || new_val || ' (으)로 변경';
	END IF;
ELSIF  old_val IS NOT NULL AND new_val IS NULL THEN
	chg_msg := '[' || name || '] ' || old_val || ' 가(이) 제거';
ELSE
	chg_msg:=NULL;
END IF;

RETURN chg_msg;
END ;
$$;


