--
-- Name: interval_format(character varying, character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: nms80; Owner: nms80
--

CREATE FUNCTION nms80.interval_format(start_time character varying, start_time_format character varying, end_time character varying, end_time_format character varying, is_confirmed character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE 
ret_time character varying :='';
current_time_v timestamp := current_timestamp AT TIME ZONE 'UTC';
year_v int;
month_v int;
day_v int;
hour_v int;
min_v int;
sec_v int;
BEGIN 
IF is_confirmed = 'Y'
	THEN current_time_v := to_timestamp(end_time, end_time_format);
END IF;	
year_v := cast(date_part('year', current_time_v- to_timestamp(start_time,start_time_format)) as int);
month_v := cast(date_part('month', current_time_v- to_timestamp(start_time,start_time_format)) as int);
day_v := cast(date_part('day', current_time_v- to_timestamp(start_time,start_time_format)) as int);
hour_v := cast(date_part('hour', current_time_v- to_timestamp(start_time,start_time_format)) as int);
min_v := cast(date_part('min', current_time_v- to_timestamp(start_time,start_time_format)) as int);
sec_v := cast(date_part('sec', current_time_v- to_timestamp(start_time,start_time_format)) as int);

IF year_v > 0 
  THEN ret_time := year_v||'year ';
END IF;
IF month_v > 0
  THEN ret_time := ret_time||month_v||'month ';
END IF;
IF day_v > 0
  THEN ret_time := ret_time||day_v||'day ';
END IF;
IF hour_v > 0
  THEN ret_time := ret_time||hour_v||'h ';
END IF;
IF min_v > 0
  THEN ret_time := ret_time||min_v||'m ';
END IF;
IF sec_v > 0
  THEN ret_time := ret_time||sec_v||'s';
END IF;
RETURN ret_time;
END;
$$;


