    LANGUAGE plpgsql
    AS $$
DECLARE 
ret_time character varying;
BEGIN 
select * from cast(to_char(to_timestamp(in_time, in_time_format) ::timestamp without time zone at time zone local_time_zone at time zone 'UTC', out_time_format)as varchar) time  into ret_time;
 RETURN ret_time;
