--
-- Name: time_diff(character varying, character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: nms80; Owner: nms80
--

CREATE FUNCTION nms80.time_diff(from_time character varying, from_time_format character varying, to_time character varying, to_time_format character varying, ret_unit character varying) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE 
ret_time character varying :='';
unit_value int := 3600;
BEGIN 
ret_unit := upper(ret_unit);
IF ret_unit = 'DAY' THEN
  unit_value := 86400;
ELSIF ret_unit = 'HOUR' THEN
  unit_value := 3600;
ELSIF ret_unit = 'MIN' THEN
  unit_value := 60;
ELSIF ret_unit = 'SEC' THEN
  unit_value := 1;
END IF;
SELECT cast(EXTRACT(epoch FROM to_timestamp(to_time, to_time_format) - to_timestamp(from_time, from_time_format))/unit_value as int) ret into ret_time;
RETURN ret_time;
END;
$$;


