CREATE VIEW nms80.ql_device AS
 SELECT s.host_name,
    s.host_alias,
    s.ip_addr,
    s.system_type,
    t1.comm_name AS system_type_name,
    s.vendor_id,
    v.vendor_name,
    s.model_nm,
    s.dev_item_detail1,
    s.dev_item_detail2,
    n.chassis_model AS model_name,
    n.ios_version AS os_version
   FROM ((nms80.sm_server s
     LEFT JOIN nms80.sm_vendor v ON ((s.vendor_id = v.vendor_id)))
     LEFT JOIN nms80.t_commcd t1 ON ((((s.system_type)::numeric = (t1.comm_cd)::numeric) AND ((t1.comm_gu)::text = 'SYSTEM_TYPE'::text)))),
    nms80.nm_device n
  WHERE ((s.host_name)::text = (n.host_name)::text);


