 SELECT l.seq_no,
    l.host_name,
    l.evt_code,
    em.evt_name,
    l.evt_level,
    el.evt_name AS evt_level_name,
    to_date((l.start_time)::text, 'yyyymmddhh24miss'::text) AS to_date,
    l.evt_msg
   FROM nms80.evt_log l,
    nms80.evt_master em,
