--
-- Name: sm_group_evt_level; Type: VIEW; Schema: nms80; Owner: nms80
--

CREATE VIEW nms80.sm_group_evt_level AS
 SELECT max((mg.group_cd)::text) AS group_cd,
    COALESCE((max(evt.evt_level))::integer, 0) AS evt_level
   FROM ( SELECT ga.host_name,
            max((ga.depth1)::text) AS depth1,
            max((ga.depth2)::text) AS depth2,
            max((ga.depth3)::text) AS depth3,
            max((ga.depth4)::text) AS depth4,
            max((ga.depth5)::text) AS depth5
           FROM ( SELECT
                        CASE
                            WHEN (c.group_depth = 1) THEN c.group_name
                            ELSE ''::character varying
                        END AS depth1,
                        CASE
                            WHEN (c.group_depth = 2) THEN c.group_name
                            ELSE ''::character varying
                        END AS depth2,
                        CASE
                            WHEN (c.group_depth = 3) THEN c.group_name
                            ELSE ''::character varying
                        END AS depth3,
                        CASE
                            WHEN (c.group_depth = 4) THEN c.group_name
                            ELSE ''::character varying
                        END AS depth4,
                        CASE
                            WHEN (c.group_depth = 5) THEN c.group_name
                            ELSE ''::character varying
                        END AS depth5,
                    a.host_name
                   FROM nms80.sm_server a,
                    nms80.mp_group_server b,
                    nms80.mp_group c
                  WHERE (((a.host_name)::text = (b.host_name)::text) AND ((b.group_cd)::text = (c.group_cd)::text) AND (c.group_depth <> 0))) ga
          GROUP BY ga.host_name) g,
    nms80.mp_group_server mg,
    nms80.nm_device n,
    (nms80.sm_server s
     LEFT JOIN ( SELECT l.host_name,
            max(l.evt_level) AS evt_level
           FROM nms80.evt_log l,
            nms80.evt_master e
          WHERE (((l.evt_code)::text = (e.evt_code)::text) AND ((l.is_confirmed)::text = 'N'::text) AND (l.evt_level = ANY (ARRAY[2, 3, 4, 5])))
          GROUP BY l.host_name) evt ON (((s.host_name)::text = (evt.host_name)::text)))
  WHERE (((s.host_name)::text = (n.host_name)::text) AND ((g.host_name)::text = (s.host_name)::text) AND ((mg.host_name)::text = (s.host_name)::text))
  GROUP BY g.depth1, g.depth2, g.depth3, g.depth4, g.depth5
  ORDER BY g.depth1, g.depth2, g.depth3, g.depth4, g.depth5;


