--
-- Name: sm_server_ip; Type: VIEW; Schema: nms80; Owner: nms80
--

CREATE VIEW nms80.sm_server_ip AS
 SELECT sm_server.host_name,
    sm_server.ip_addr
   FROM nms80.sm_server
UNION
 SELECT sm_mapping_ip.host_name,
    sm_mapping_ip.ip_addr
   FROM nms80.sm_mapping_ip
UNION
 SELECT s.host_name,
    a.if_ip AS ip_addr
   FROM nms80.nm_if_addr a,
    nms80.sm_server s,
    nms80.nm_device n,
    nms80.nm_if_search i
  WHERE (((a.ip_addr)::text = (s.ip_addr)::text) AND ((s.host_name)::text = (n.host_name)::text) AND ((a.ip_addr)::text = (i.ip_addr)::text) AND (a.port_inst = i.port_inst) AND (i.admin_status = 1) AND (i.op_status = 1) AND ((s.isusesystem)::text = 'Y'::text) AND (n.enable = 'Y'::bpchar));


ALTER VIEW nms80.sm_server_ip OWNER TO nms80;

